<script>
	const NAV_DIALOG = document.querySelector("#nav-dialog");
	const SHOW_BUTTON = document.querySelector("#nav-show-btn");
	const HIDE_BUTTON = document.querySelector("#nav-hide-btn");

	SHOW_BUTTON.addEventListener("click", () => {
		document.body.classList.add("overflow-hidden");
		NAV_DIALOG.dataset.visible = true;
	});

	HIDE_BUTTON.addEventListener("click", () => {
		document.body.classList.remove("overflow-hidden");
		NAV_DIALOG.dataset.visible = false;
	});
